# Works with the LEGO SPIKE app and the LEGO MINDSTORMS app

from ubinascii import hexlify, a2b_base64
from uhashlib import sha256
from os import mkdir


encoded=(('matrix_v2.mpy', ('\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n', 'FDXqf1sUPGxpc3Rjb21wPgUBAAAFAAVmBTFlLTA3gXAHABACAAAMhAFRAZECJycsAAD/sB47ZgDAsB4CVGYAwhwLsB4XZgBkAcOzsrDz8R4lZgCy87HzWxxsaW5fcmVncmVzc2lvbg0AAAJ4AnmBdAgAEAMAAAyGAVEBkQkoJywAAP+wHhpwb2x5bm9taWFsaXplsmYBwLAeEWYAwxwRsB4RZgBkAcS0s7Dz8R4RZgCz87HzWx5wb2x5X3JlZ3Jlc3Npb24RAAAREQxkZWdyZWU=\n'), '59a5727b2742f224289a750d1d6d022ac66f06b952a0bbd3c3510b4554eec04c'),)

def calc_hash(b):
    return hexlify(sha256(b).digest()).decode()

error=False
# try:
#     mkdir('/projects/mpy_robot_tools')
# except:
#     pass

for file, code, hash_gen in encoded:
    print("Writing file ", file)
    # hash_gen=code[1]
    target_loc = '/'+file
    
    print('writing '+file+' to folder /')
    with open(target_loc,'wb') as f:
        for chunk in code:
            f.write(a2b_base64(chunk))
    del code

    try:
        print('Finished writing '+file+', Checking hash.')
        result=open(target_loc,'rb').read()
        hash_check=calc_hash(result)

        print('Hash generated: ',hash_gen)

        if hash_check != hash_gen:
            print('Failed hash of .mpy on SPIKE: '+hash_check)
            error=True
    except Exception as e:
        print(e)


if not error:
    print('Library written succesfully. Reset your hub.')
else:
    print('Failure in writing library!')
